import React, { Component } from 'react';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTint, faUndoAlt, faSeedling, faPlus } from '@fortawesome/free-solid-svg-icons'

import FlowerCard from './FlowerCard';

class FlowersSection extends Component {
    constructor(){
        super();

        this.state = {
            flowers: []
        }
      }

      componentDidMount() {
        this.fetchFlowers();
      }

      fetchFlowers = () => {
        fetch('/flowers/all')
        .then(response => response.json())
        .then(result => {
            this.setState({flowers: result})
        })
        .catch(e => {
            console.log(e);
        });
      }

      flowerItems = () => {
          let rl = [];

          for (let f in this.state.flowers){
              console.log(f);
              let flow = this.state.flowers[f];
              rl.push(<FlowerCard title={flow.title} waterTime={flow.waterTime} />);
          }

          return rl;
      }

      pushNewFlower = () => {
              // Simple POST request with a JSON body using fetch
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ Title: 'New Flower', WaterTime: 45})
        };
        fetch('/flowers/add', requestOptions)
            .then(response => response.json())
            .then(data => {
                console.log(data);
            });
      }

      removeFlower = () => {
                    // Simple POST request with a JSON body using fetch
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ID: 2})
        };
        fetch('/flowers/remove', requestOptions)
            .then(response => response.json())
            .then(data => {
                console.log(data);
            });
      }

    render () {
        return (
            <div className='mt-20 w-full p-6 border-gray-200 text-left rounded-2xl'>
                <div className='-mt-3 p-3 flex justify-between border-b-2 mb-6'>
                    <h3 className='text-4xl font-bold'>Plants</h3>
                    <div className='px-3 py-2 hover:bg-gray-100 rounded-xl flex justify-evenly border-2 border-green-500'>
                        <h1 className='pr-2 font-bold'>Add</h1>
                        <FontAwesomeIcon icon={faPlus} size='lg' />
                    </div>
                </div>
                <div className='row flex flex-wrap gap-4'>
                    {this.flowerItems()}
                </div>
            </div>
        );
    }
}

export default FlowersSection;
