import React from 'react';
import FlowerCard from '../components/FlowerCard';

class Landing extends React.Component {

  constructor(){
    super();

    this.state = {
        flowers: []
    }
  }

  componentDidMount() {
    this.fetchFlowers();
  }

  fetchFlowers = () => {
    fetch('/flowers/all')
    .then(response => response.json())
    .then(result => {
        this.setState({flowers: result})
    })
    .catch(e => {
        console.log(e);
    });
  }

  flowerItems = () => {
      let rl = [];

      for (let f in this.state.flowers){
          rl.push(<FlowerCard title={f.title} waterTime={f.waterTime} />);
      }

      return rl;
  }

  render (){
    return (
        <div className="text-center m-24 max-w-4xl mx-auto p-3">
            <div className="p-4 font-bold text-5xl">
                <span className='text-blue-500 font-mono'>Watermango</span> is a easy way to keep your office <span className="text-green-500">plants</span> watered</div>

            <div className='mt-20 w-full p-6 border-2 border-gray-200 rounded-3xl text-left'>
                <h3 className='-mt-3 p-2 text-2xl font-bold'>Flowers</h3>
                <div className='row flex flex-wrap gap-4'>
                    {this.flowerItems}
                </div>
            </div>
        </div>
    );
  }
}

export default Landing;
