import React, { Component } from 'react';

class FlowerCard extends Component {

  render () {
    return (
        <div className='w-64'>
            <div className='w-full h-80 p-3 bg-gray-50 rounded-3xl mx-auto hover:shadow-sm border border-gray-50 hover:border-gray-200 cursor-pointer'>
                <div className='grid grid-cols-2 gap-2'>
                    <div className="w-24 h-24 bg-gray-100 rounded-3xl"></div>
                    <div className="w-24 h-24 rounded-3xl p-1">
                        <div className='text-xs font-bold text-gray-600'>next watering</div>
                        <div className='text-3xl'><span className='text-gray-300'>in</span> {this.props.waterTime}</div>
                        <div className='text-sm text-gray-400'>min</div>
                    </div>
                </div>
                <div className='text-base h-16 font-bold p-2 overflow-auto'>{this.props.title}</div>
                <div className='grid grid-cols-2 gap-1 p-2'>
                    <div className='p-3 py-5 px-6 border-2 border-blue-500 text-lg rounded-xl bg-blue-50 text-blue-500 font-bold'>Water</div>
                    <div className='p-3 py-5 px-6 border-2 border-green-500 text-lg rounded-xl'>Water</div>
                </div>
            </div>
        </div>
    );
  }
}

export default FlowerCard;
